htmlform.script:
		$j(document).ready(function(){
			var pbirthdate = new Date("").getTime();
			var pweight = ""
			var pheight = ""
			//check if the date of tb treatment already exists
			var treatmentDate = ""
			setValue('weight.value', pweight);
			setValue('height.value', pheight);
			//set the value of the treatment date if available
			setValue('dstartoftreatment.value', treatmentDate);

			var pulmodiv = document.getElementById('iddivPulmonary');
			var expulmodiv = document.getElementById('expul');
			//getting the values of the disease classification option buttons
			var optValues = getValue('dclassification.value');
				if (optValues == 42){
					$('#expul :input').prop('disabled', true);
    				$('#iddivPulmonary :input').prop('disabled', false);
				}
				else if (optValues == 5042 ){
					$('#iddivPulmonary :input').prop('disabled', true);
    				$('#expul :input').prop('disabled', false);
				}
				else {
					$('#iddivPulmonary :input').prop('disabled', true);
    				$('#expul :input').prop('disabled', true);
				}

    			$('#dclassification input:radio').click(function() {
    				if ($(this).val() == 42) {
    					$('#expul :input').prop('disabled', true);
    					$('#iddivPulmonary :input').prop('disabled', false);
    				}
    				if ($(this).val() == 5042) {
    					$('#iddivPulmonary :input').prop('disabled', true);
    					$('#expul :input').prop('disabled', false);
    				}
    			});
   			//activating the transfer in details only when the patient classification is transfer in
   			//patientClassification
   			var optpatientclassification = getValue('patientClassification.value');
   				if (optpatientclassification == 159872 ) {
   					$('#transferInDetails :input').prop('disabled', false);
   				}
   				else {
   					$('#transferInDetails :input').prop('disabled', true)
   				}

   				$('#patientClassification input:radio').click(function() {
   					if ($(this).val() == 159872){
   						$('#transferInDetails :input').prop('disabled', false);
   					}
   					else {
   						$('#transferInDetails :input').prop('disabled', true);
   					}
   				});
 			//hide the div tags that hold errors on the start of the form
 			$('#startOfTreatmentDateError').hide();
 			$('#referalDateError').hide();
 			$('#transferInDatesError').hide();

 			//control the transfer in option 
 			$('#referredByOptions1 input:radio').click(function() {
 				$( '#referredByOptions2 input:radio' ).prop( "checked", false );
 			});
 			$('#referredByOptions2 input:radio').click(function() {
 				$( '#referredByOptions1 input:radio' ).prop( "checked", false );
 			});


			//create some variable that will control the dates on the form not to be earlier than DOB
			var pbirthdate = new Date("").getTime();
			var encounterdate;
			var dstartoftreatment;
			var dreferredby;
			var transferindate;
			var dfirstenrolled;
            var treatmentSupporterNumber;
            var regex = /^\+?\d{7,13}$/

			beforeSubmit.push (function() {
				encounterdate = new Date(getValue('encounterdate.value')).getTime();
       			datestartoftreatment = new Date(getValue('dstartoftreatment.value')).getTime();
       			dreferredby = new Date(getValue('datereferredby.value')).getTime();
       			transferindate  = new Date(getValue('transferindate.value')).getTime();
       			dfirstenrolled = new Date(getValue('datefirstenrolled.value')).getTime();
                treatmentSupporterNumber = getValue('treatmentSupportedNumber.value');

                if (treatmentSupporterNumber != '' && !regex.test(treatmentSupporterNumber)){
                    $('#treatmentsupportError').html('Please provide valid phone Number');
                    return false;
                }
       			if (encounterdate < pbirthdate) {
               		getField('encounterdate.error').html('Encounter datetime should be after the birth date').show();
                   	return false
         		}

          		if (datestartoftreatment < pbirthdate) {
          			$('#startOfTreatmentDateError').show();
               		$('#startOfTreatmentDateError').html('Date start of treatment should be after the birth date');
                   	return false
         		}

        		if (dreferredby < pbirthdate) {
        			$('#referalDateError').show();
               		$('#referalDateError').html('Date referred by should be after the birth date');
                   	return false
         		}

         		if (transferindate < pbirthdate) {
         			$('#transferInDatesError').show();
               		$('#transferInDatesError').html('Transfer in date should be after the birth date');
                   	return false
         		}
          		if (dfirstenrolled < pbirthdate){
          			$('#transferInDatesError').show();
               		$('#transferInDatesError').html('Date first enrolled should be after the birth date');
                   	return false
         		}

    			return true;
			});
		});
